mapper.select:
select
b.bh,b.mc,b.dev_lb,b.dev_id
from st_device b,st_dev_module c

  c.module_id='0036' and c.dev_serial=b.bh  and b.dev_lb=2

    and b.bh = #{bh,jdbcType=CHAR}



    and b.mc like #{mcLike}




    and b.ip like #{ipLike}



    and b.dev_lb = #{devLb,jdbcType=TINYINT}



    and b.dev_id like #{devIdLike}



    and b.dev_sb_id = #{devSbId,jdbcType=BIGINT}



    and b.dev_logic_bh = #{devLogicBh,jdbcType=INTEGER}


    and b.dlx = #{dlx,jdbcType=INTEGER}



    and (b.bh like #{keysLike}
    or b.mc like #{keysLike}
    or b.ip like #{keysLike}
    or b.devId like #{keysLike}
    )


order by b.bh

select
b.bh,b.mc,b.dev_lb,b.ip,case when d.dev_order is null then 0 else 1 end as isUse
from st_device b inner join st_dev_module c on c.dev_serial=b.bh
left join st_doorfx_real d on b.bh= d.dev_serial and d.dev_order=9 and d.fx=0

  c.module_id='0036' and b.dev_lb=1

    and b.bh = #{bh,jdbcType=CHAR}



    and b.mc like #{mcLike}




    and b.ip like #{ipLike}



    and b.dev_lb = #{devLb,jdbcType=TINYINT}



    and b.dev_id like #{devIdLike}



    and b.dev_sb_id = #{devSbId,jdbcType=BIGINT}



    and b.dev_logic_bh = #{devLogicBh,jdbcType=INTEGER}


    and b.dlx = #{dlx,jdbcType=INTEGER}



    and (b.bh like #{keysLike}
    or b.mc like #{keysLike}
    or b.ip like #{keysLike}
    or b.devId like #{keysLike}
    )


order by b.bh

