mapper.select:
select

, head_image
from dt_user t1 left join (select user_serial as tmp_serial, head_image from user_info_extend) t2 on
t1.user_serial = t2.tmp_serial


        and user_serial = #{userSerial,jdbcType=BIGINT}



        and user_no like #{userNoLike}





        and user_lname = #{userLname,jdbcType=NVARCHAR}



        and user_fname like #{userFnameLike}




        and dep_no like #{depNoLike}



        and user_dep = #{userDep,jdbcType=INTEGER}



        and user_depname like #{userDepnameLike}



        and user_workday = #{userWorkday,jdbcType=TIMESTAMP}



        and user_duty like #{userDutyLike}




        and user_card like #{userCardLike}




        and user_finger like #{userFingerLike}




        and user_password like #{userPasswordLike}



        and pwd_begin = #{pwdBegin,jdbcType=TIMESTAMP}


        and pwd_end = #{pwdEnd,jdbcType=TIMESTAMP}





        and user_type < 51


        and pact_begin = #{pactBegin,jdbcType=TIMESTAMP}


        and pact_end = #{pactEnd,jdbcType=TIMESTAMP}


        and user_level = #{userLevel,jdbcType=INTEGER}


        and user_photo = #{userPhoto,jdbcType=INTEGER}



        and user_sex like #{userSexLike}




        and user_nation like #{userNationLike}




        and user_xueli like #{userXueliLike}




        and user_native like #{userNativeLike}



        and user_birthday = #{userBirthday,jdbcType=TIMESTAMP}



        and user_id like #{userIdLike}




        and user_post like #{userPostLike}




        and user_linkman like #{userLinkmanLike}





        and user_telephone = #{userTelephone,jdbcType=NVARCHAR}



        and user_address like #{userAddressLike}




        and user_email like #{userEmailLike}




        and user_1 like #{user1Like}




        and user_2 like #{user2Like}




        and user_bz like #{userBzLike}




        and kq_rule like #{kqRuleLike}




        and kq_taoban like #{kqTaobanLike}



        and kq_tiaoxiu = #{kqTiaoxiu,jdbcType=INTEGER}


        and xf_time = #{xfTime,jdbcType=TIMESTAMP}


        and xf_jiange = #{xfJiange,jdbcType=INTEGER}


        and xf_je = #{xfJe,jdbcType=INTEGER}


        and user_sj = #{userSj,jdbcType=TIMESTAMP}



        and user_rank like #{userRankLike}



        and gly_img = #{glyImg,jdbcType=INTEGER}


        and user_ac = #{userAc,jdbcType=INTEGER}



        and user_gsbh like #{userGsbhLike}




        and user_yglx like #{userYglxLike}




        and user_zhbh like #{userZhbhLike}




        and user_zhlx like #{userZhlxLike}




        and user_txm like #{userTxmLike}



        and user_lx = #{userLx,jdbcType=INTEGER}


        and user_mj = #{userMj,jdbcType=INTEGER}


        and user_identity = #{userIdentity,jdbcType=INTEGER}


        and user_face = #{userFace,jdbcType=INTEGER}



        and lx like #{lxLike}

with temp (dep_parent, dep_parent_name, dep_serial, dep_name) as (select dep_serial, dep_name, dep_serial, dep_name from dt_dep union all select t2.dep_parent, t2.dep_parent_name, t1.dep_serial, t1.dep_name from dt_dep t1, temp t2 where t1.dep_parent = t2.dep_serial) select t2.* from temp t1, dt_user t2 where t1.dep_serial = t2.user_dep and t2.user_type < 51 and dep_parent in
#{item}
order by dep_parent
